onClick:
{{
  (() => {
    const ids = MultiSelectItens.selectedOptionValues;

    if (!ids || ids.length === 0) {
      showAlert("Selecione pelo menos um item", "warning");
      return;
    }

    desativar_itens_checklist.run(
      () => {
        showAlert("Itens desativados com sucesso", "success");

        // Fecha o modal APÃ“S salvar
        closeModal("del_att_item");

        // Recarrega a lista
        catalogo_itens.run();
      },
      (err) => {
        showAlert("Erro ao desativar itens", "error");
        console.error(err);
      }
    );
  })()
}}
