{{
(() => {
  const parseBR = v => {
    if (v === null || v === undefined) return NaN;
    if (typeof v === "number") return v;
    let s = String(v).trim();
    if (s === "") return NaN;

    // remove espaços e caracteres não-numéricos exceto . , e -
    s = s.replace(/\s/g, "").replace(/[^0-9.,-]/g, "");

    if (s.includes(".") && s.includes(",")) {
      // ponto = milhar, vírgula = decimal
      s = s.replace(/\./g, "").replace(/,/g, ".");
    } else if (s.includes(",")) {
      s = s.replace(/,/g, ".");
    } else if ((s.match(/\./g) || []).length > 1) {
      s = s.replace(/\./g, "");
    }

    const n = Number(s);
    return isNaN(n) ? NaN : n;
  };

  const rawA = Input_precounitario.text;
  const rawB = Input_quantidade.text;
  const rawC = Input_precopecaservico.text; // <-- novo campo serviço

  const hasA = rawA && String(rawA).trim() !== "";
  const hasB = rawB && String(rawB).trim() !== "";
  const hasC = rawC && String(rawC).trim() !== "";

  if (!hasA || !hasB) return null; // precisa de unitário e quantidade

  const a = parseBR(rawA);
  const b = parseBR(rawB);
  const c = hasC ? parseBR(rawC) : 0; // serviço opcional, assume 0 se vazio

  if (isNaN(a) || isNaN(b) || isNaN(c)) return null;

  const total = (a * b) + c;

  return total.toLocaleString("pt-BR", { style: "currency", currency: "BRL" });
})()
}}
