listData:
{{
  (() => {
    const checklist = checklist_diario_motos_list.data || [];
    const filiais = cod_filial.data || [];
    const condutores = conduto_absatecimento.data || [];

    return checklist
      .filter(item => String(item.resultado_final || "").trim() === "NAO_APTO")
      .map(item => {

        // Mapa dos itens do checklist
        const mapaItens = {
  nivel_combustivel: "Nível de combustível",
  farol: "Farol",
  luz_freio: "Luz de freio",
  setas: "Setas",
  pneu: "Pneu dianteiro",
  pneu_traseiro: "Pneu traseiro",
  vazamento_oleo: "Vazamento de óleo",
  freio_dianteiro: "Freio dianteiro",
  freio_traseiro: "Freio traseiro",
  manetes_pedaleiras: "Manetes e pedaleiras",
  espelhos: "Espelhos",
  buzina: "Buzina",
  documentacao_crlv: "Documentação (CRLV)"
};

        const itensNC = Object.keys(mapaItens)
          .filter(campo => String(item[campo] || "").trim() === "NC");

        // Lookup filial (Oracle)
        const filial = filiais.find(
          f => String(f.CODIGO) === String(item.filial_id)
        );

        // Lookup condutor (Oracle)
        const condutor = condutores.find(
          c => String(c.MATRICULA) === String(item.condutor_id)
        );

        // Data formatada
        const dataHora = (() => {
          const normalizado = String(item.data_hora || "").replace(/\.\d+$/, "");
          const m = moment(
            normalizado,
            ["YYYY-MM-DD HH:mm:ss", moment.ISO_8601],
            true
          );
          return m.isValid()
            ? m.format("DD/MM/YYYY HH:mm")
            : normalizado;
        })();

        return {
          id_checklist: item.id_checklist,
          placa: item.placa,
          data_hora: dataHora,
          filial: filial?.CIDADE_CODIGO || "—",
          condutor: condutor?.NOME || "—",
          qtd_nc: itensNC.length,
          observacoes: item.observacoes || ""
        };
      });
  })()
}}
