onClick:
{{ 
  nova_peca.run()
    .then((res) => {

      // Caso 1: não inseriu nada (ON CONFLICT DO NOTHING)
      if (
        nova_peca.responseMeta?.rowsAffected === 0 ||
        (Array.isArray(res) && res.length === 0)
      ) {
        showAlert("⚠️ Esta peça já está cadastrada.", "warning");
        return;
      }

      // Caso 2: inseriu com sucesso
      showAlert("✅ Peça adicionada com sucesso!", "success");
      resetWidget("Input_peca_add");
      closeModal(adicionar_peca.name);
    })
    .catch((err) => {

      // Caso 3: erro real
      const msg = err?.message || err?.error || "Erro inesperado ao cadastrar a peça.";
      showAlert(`❌ ${msg}`, "error");
    })
}}
