onFilesSelected:
{{
  (async () => {
    // 1Ô∏è‚É£ Faz upload das novas fotos
    const novasFotos = await inserir_checklist_fotos.uploadNovasFotos();

    // 2Ô∏è‚É£ Valida√ß√£o
    if (!Array.isArray(novasFotos) || novasFotos.length === 0) {
      showAlert("Nenhuma nova foto para salvar", "warning");
      return;
    }

    // 3Ô∏è‚É£ Salva no store (ESSENCIAL)
    await storeValue("novas_fotos_urls", novasFotos);

    console.log("üß™ novas_fotos_urls (store):", appsmith.store.novas_fotos_urls);

    // 4Ô∏è‚É£ Atualiza no banco (concatena no JSONB)
    await update_checklist_diario_from_t.run();

    showAlert("üì∏ Fotos adicionadas ao checklist com sucesso!", "success");

    // 5Ô∏è‚É£ Limpa o filepicker
    resetWidget("atualizar_checklist_fotos", true);

  })().catch(err => {
    console.error("‚ùå ERRO AO INSERIR NOVAS FOTOS:", err);
    showAlert("Erro ao salvar fotos no checklist", "error");
  })
}}
