image:
{{
  (() => {
    try {
      const link = appsmith.store.itemSelecionado?.link_imagem_orcamento;
      if (!link || link === 'null' || link === '[]' || String(link).trim() === '') return '';
      const parsed = JSON.parse(link);
      return Array.isArray(parsed) && parsed.length > 0 ? parsed[0] : (link || '');
    } catch (e) {
      return '';
    }
  })()
}}

isVisible:
{{
  (() => {
    try {
      const link = appsmith.store.itemSelecionado?.link_imagem_orcamento;
      if (!link || link === 'null' || link === '[]' || String(link).trim() === '') return false;
      return true;
    } catch (e) {
      return false;
    }
  })()
}}
